									else
									{
										jCell.addClass("cl-results-incorrect");
									}
									jCell = $(document.createElement("td")).addClass("cl-results-q-variants-text").html(fnGetText({ sHTML: $(jxVars[k-1]).children("q_variant").text() })).appendTo(jRow);
								}
							}
						}
						break;
					}
					case "numeric":
					case "fib":
					{
						var jxVars = oArgs.jxParams.find("q_variants_" + oArgs.sType + ":first > item");
						var jxCond;
						var sOp;
						var aCond;
						var oSigns = { "eq": "=", "ne": "!=", "lt": "&lt;", "lte": "&lt;=", "gt": "&gt;", "gte": "&gt;=" };
						if(oArgs.oStore.R!=null && oArgs.oStore.R.length!=0)
						{
							for(var k=1; k<=jxVars.length; k++)
							{
								jRow = $(document.createElement("tr")).appendTo(oArgs.jVarTable);
								jCell = $(document.createElement("td")).addClass("cl-results-q-variants-text").attr({ "data-var-id": k }).html(oArgs.oStore.R[k-1]).appendTo(jRow);
								if(oCurObj.data.bDisplayCorrects)
								{
									if(CL.Q.EvalSet({ sType: oArgs.sType, sValue: oArgs.oStore.R[k-1], oPattern: oArgs.oStore.P[k-1] })==true)
									{
										jCell.addClass("cl-results-correct");
									}
									else
									{
										jCell.addClass("cl-results-incorrect");
									}
									aCond = [];
									sOp = $(jxVars[k-1]).children("q_cond_logic").text();
									jxCond = $(jxVars[k-1]).find("q_conditions > item");
									if(oArgs.sType=="numeric")
									{
										for(var j=0; j<jxCond.length; j++)
										{
											aCond.push( oSigns[$(jxCond[j]).children("q_condition").text()] + $(jxCond[j]).children("q_variant").text() );
										}
									}
									else
									{
										for(var j=0; j<jxCond.length; j++)
										{
											switch($(jxCond[j]).children("q_condition").text())
											{
												case "eq":
												{
													aCond.push( "=" + $(jxCond[j]).children("q_variant").text() );
													break;
												}
												case "bw":
												{
													aCond.push( $(jxCond[j]).children("q_variant").text() + "*");
													break;
												}
												case "eq":
												{
													aCond.push( "*" + $(jxCond[j]).children("q_variant").text() );
													break;
												}
												case "eq":
												{
													aCond.push( "*" + $(jxCond[j]).children("q_variant").text() + "*" );
													break;
												}
											}
										}
									}
									$(document.createElement("td")).addClass("cl-results-q-variants-text").html( aCond.join(" " + sOp + " ") ).appendTo(jRow);
								}
							}
						}
						break;
					}
					case "oto":
					{
						var jxVars = oArgs.jxParams.find("q_variants_oto:first > item");
						if(oArgs.oStore.R!=null && oArgs.oStore.R.length!=0)
						{
							for(var k=1; k<=jxVars.length; k++)
							{
								jRow = $(document.createElement("tr")).attr({ "data-var-id": k }).appendTo(oArgs.jVarTable);
								jCell = $(document.createElement("td")).addClass("cl-results-q-variants-text").attr({ "data-var-id": k }).html(fnGetText({ sHTML: $(jxVars[k-1]).children("q_target").text() })).appendTo(jRow);
								if(oCurObj.data.bDisplayCorrects)
								{
									if(+oArgs.oStore.R[k-1]==k)
									{
										jCell.addClass("cl-results-correct");
									}
									else
									{
										jCell.addClass("cl-results-incorrect");
									}
								}
								if(oArgs.oStore.R[k-1]!="" && !isNaN(+oArgs.oStore.R[k-1]))
								{
									jCell = $(document.createElement("td")).addClass("cl-results-q-variants-text").attr({ "data-var-id": k }).html(fnGetText({ sHTML: $(jxVars[+oArgs.oStore.R[k-1]-1]).children("q_bullet").text() })).appendTo(jRow);
									if(oCurObj.data.bDisplayCorrects)
									{
										if(+oArgs.oStore.R[k-1]==k)
										{
											jCell.addClass("cl-results-correct");
										}
										else
										{
											jCell.addClass("cl-results-incorrect");
										}
									}
								}
								else
								{
									jCell = $(document.createElement("td")).addClass("cl-results-q-variants-text").attr({ "data-var-id": k }).html("&nbsp;").appendTo(jRow);
								}
								if(oCurObj.data.bDisplayCorrects)
								{
									jCell = $(document.createElement("td")).addClass("cl-results-q-variants-text").attr({ "data-var-id": k }).html(fnGetText({ sHTML: $(jxVars[k-1]).children("q_bullet").text() })).appendTo(jRow);
								}
							}
						}
						break;
					}
				}
			}

			var jxObjList = oCurObj.jxParams.children("objects").children("item");
			if(jxObjList.length!=0 && oCurObj.data.bCreated)
			{
				var jContainer = jCurObjDiv.find(".cl-results-container");
				var jQTemplate = jCurObjDiv.find(".cl-template-storage .cl-results-q-template");
				var jTTemplate = jCurObjDiv.find(".cl-template-storage .cl-results-t-template");
				var jQBlock;
				var jTBlock;
				var sMainObj = "";
				if(oCurObj.data.bDisplayMainScore)
				{
					for(var sKey in CLJ)
					{
						if(+CLJ[sKey].bModule==1)
						{
							sMainObj = sKey;
							break;
						}
					}
				} 
				var jTxtDiv = $(document.createElement("div"));
				jxObjList.each(function ()
				{
					var sId = $(this).children("objid").text();
					var jxObject = CL.jxModule.find("object[id='" + sId + "']");
					var jxParams = CL.jxParams.children("param[objectid='" + sId + "']");
					var bDisplayHeader = ($(this).children("displayobjheader").text()=="yes");
					var jxObjectives;
					var sTxt;
					if(jxObject.length>0)
					{
						var sType = jxObject.attr("type");
						if(sType=="q_0000" || sType=="q_0001")
						{
							if(sType=="q_0001")
							{ 
								if(CLZ.oStore[sId]!=null)
								{ 
									var jTBlock = jContainer.find("div[data-o-id='" + sId + "']");
									var jContents = jTBlock.children(".cl-results-t-contents").html("");
									var jxTestParams = CL.jxParams.children("param[objectid='" + sId + "']");
									var jxQuestions = jxTestParams.find("q_questions:first > item");
									var jxQuestion;
									var sQId;
									var jxQParams;
									for(var i=0; i<jxQuestions.length; i++)
									{
										sQId = sId + "_" + i; 
										jQBlock = jQTemplate.clone(true).addClass("cl-results-q").attr({ "data-o-id": sId, "data-o-type": sType, "data-q-id": i, "data-o-toggle": "1" }).appendTo(jContents).show();
										if(bDisplayHeader)
										{
											jQBlock.find(".cl-results-header .cl-results-header-title").html($(this).children("objheader").text() + " (" + (i+1) + "/" + jxQuestions.length + ")");
										}
										else
										{
											jQBlock.find(".cl-results-header .cl-results-header-title").html(oCurObj.jxParams.children("text_objecttype_test").text() + " " + sId + " (" + (i+1) + "/" + jxQuestions.length + ")");
										}
										if(CLZ.oStore[sId].Q[i]!=null)
										{
											jxQParams = $(jxQuestions[i]);
											switch(CLZ.oStore[sId].Q[i].u)
											{
												case "c":
												{
													jQBlock.find(".cl-switch").show().on("click", function (e) { oCurObj.CallMethod({ sMethod: "Toggle", oMethodArgs: { oElem: this, oEvt: e } }) });
													jQBlock.find(".cl-results-contents").show();
													jQBlock.find(".cl-results-na").hide();
													jQBlock.find(".cl-results-header-score").show();
													var sType = jxQParams.children("question_type").text();
													if(sMainObj!="")
													{
														if(CLJ[sMainObj]!=null)
														{
															if(oCurObj.data.bDisplayMainScore && CLJ[sMainObj].oSrc[sQId]!=null)
															{
																jQBlock.find(".cl-results-header-score-div").html(CLJ[sMainObj].oSrc[sQId].nRawScore);
															}
														}
													}
													if(oCurObj.data.bDisplayQuestion)
													{
														jQBlock.find(".cl-results-q-text").html(fnGetText({ sHTML: jxQParams.children("text_main").text() }));
													}
													if(oCurObj.data.bDisplayStats)
													{
														var oOldType = { "true-false": "tf", "choice": "choice", "select": "select", "order": "range", "numeric": "numeric", "fib": "text", "oto": "oto", "otm": "otm", "mtm": "mtm" };
														var oScoring = { "true-false": "tf", "choice": "choice", "select": "choice", "order": "choice", "numeric": "choice", "fib": "choice", "oto": "choice", "otm": "choice", "mtm": "choice" };
														jQBlock.find(".cl-stats-q-type").html(oCurObj.jxParams.children("text_objecttype_" + oOldType[sType]).text());
														var sScoring = jxQParams.children("q_scoring_type_" + oScoring[sType]).text();
														jQBlock.find(".cl-stats-q-scoring").html(oCurObj.jxParams.children("text_constant_scoringtype").text() + " " + oCurObj.jxParams.children("text_scoringtype_" + sScoring).text());
														if(oCurObj.data.bDisplayAttempts)
														{
															var iAttemptsLeft = 0;
															for(var j=0; j<CLZ.oStore[sId].Q[i].A.length; j++)
															{
																if(CLZ.oStore[sId].Q[i].A[j].u=="c") continue;
																iAttemptsLeft = CLZ.oStore[sId].Q[i].A.length - j;
																break;
															}
															jQBlock.find(".cl-stats-q-attempts").html(oCurObj.jxParams.children("text_constant_attemptsmax").text() + " " + CLZ.oStore[sId].Q[i].A.length + "<br/>" + oCurObj.jxParams.children("text_constant_attemptsleft").text() + " " + iAttemptsLeft);
														}
														if(oCurObj.data.bDisplayTimers && CLZ.oStore[sId].Q[i].t!=null)
														{
															jQBlock.find(".cl-stats-q-timing").html(oCurObj.jxParams.children("text_constant_duration").text() + " " + CLZ.oStore[sId].Q[i].t + "<br/>" + oCurObj.jxParams.children("text_constant_latency").text() + " " + CLZ.oStore[sId].Q[i].e);
														}
														else
														{
															jQBlock.find(".cl-stats-q-timing").hide();
														}
														if(oCurObj.data.bDisplayScores)
														{
															jxObjectives = jxQParams.find("q_scoreboards > item");
															var sObjId;
															var jRow;
															var jCell;
															var jScoreTable = jQBlock.find(".cl-stats-obj-table");
															if(jScoreTable.children("tbody").length!=0)
															{
																jScoreTable = jScoreTable.children("tbody:first");
															}
															for(var j=0; j<jxObjectives.length; j++)
															{
																sObjId = $(jxObjectives[j]).children("q_board").text();
																jRow = jScoreTable.children("tr[data-obj-id='" + sObjId + "']");
																if(jRow.length==0)
																{
																	jRow = $(document.createElement("tr")).attr({ "data-obj-id": sObjId }).appendTo(jScoreTable);
																	$(document.createElement("td")).addClass("cl-stats-obj-obj").attr({ "data-obj-id": sObjId }).html(CLJ[sObjId].sName).appendTo(jRow);
																	$(document.createElement("td")).addClass("cl-stats-obj-score").attr({ "data-obj-id": sObjId }).appendTo(jRow);
																}
																if(CLJ[sObjId].oSrc[sQId]!=null)
																{
																	jRow.children(".cl-stats-obj-score").html(CLJ[sObjId].oSrc[sQId].nRawScore);
																}
																else
																{
																	jRow.children(".cl-stats-obj-score").html("0");
																}
															}
														}
														else
														{
															jQBlock.find(".cl-stats-right").hide();
														}
													}
													if(oCurObj.data.bDisplayVariants)
													{
														var jVarTable = jQBlock.find(".cl-results-q-variants-table");
														if(jVarTable.length!=0)
														{
															jVarTable.remove();
														}
														jVarTable = $(document.createElement("table")).addClass("cl-results-q-variants-table").appendTo(jQBlock.find(".cl-results-variants .cl-results-contents"));
														jVarTable = $(document.createElement("tbody")).appendTo(jVarTable);
														fnFillVars({ sId: sQId, sType: sType, jVarTable: jVarTable, jxParams: jxQParams, oStore: CLZ.oStore[sId].Q[i] });
													}
													break;
												}
												case "i":
												{
													jQBlock.find(".cl-switch").hide();
													jQBlock.find(".cl-results-contents").hide();
													jQBlock.find(".cl-results-na").show();
													jQBlock.find(".cl-results-header-score").hide();
													break;
												}
											}
										}
										else
										{
											jQBlock.find(".cl-switch").hide();
											jQBlock.find(".cl-results-contents").hide();
											jQBlock.find(".cl-results-na").show();
											jQBlock.find(".cl-results-header-score").hide();
										}
									}
								}
							}
							else
							{
								jQBlock = jContainer.find("div[data-o-id='" + sId + "']");
								if(CLZ.oStore[sId]!=null)
								{
									switch(CLZ.oStore[sId].u)
									{
										case "c":
										{
											var sType = jxParams.children("question_type").text();
											if(sMainObj!="")
											{
												if(CLJ[sMainObj]!=null)
												{
													if(oCurObj.data.bDisplayMainScore && CLJ[sMainObj].oSrc[sId]!=null)
													{
														jQBlock.find(".cl-results-header-score-div").html(CLJ[sMainObj].oSrc[sId].nRawScore);
													}
												}
											}
											if(oCurObj.data.bDisplayQuestion)
											{
												jTxtDiv.html(jxParams.children("text_main").text());
												jQBlock.find(".cl-results-q-text").html(jTxtDiv.text());
											}
											if(oCurObj.data.bDisplayStats)
											{
												var oOldType = { "true-false": "tf", "choice": "choice", "select": "select", "order": "range", "numeric": "numeric", "fib": "text", "oto": "oto", "otm": "otm", "mtm": "mtm" };
												var oScoring = { "true-false": "tf", "choice": "choice", "select": "select", "order": "match", "numeric": "inputs", "fib": "inputs", "oto": "match", "otm": "match", "mtm": "match" };
												jQBlock.find(".cl-stats-q-type").html(oCurObj.jxParams.children("text_objecttype_" + oOldType[sType]).text());
												var sScoring = jxParams.children("q_scoring_type_" + oScoring[sType]).text();
												jQBlock.find(".cl-stats-q-scoring").html(oCurObj.jxParams.children("text_constant_scoringtype").text() + " " + oCurObj.jxParams.children("text_scoringtype_" + sScoring).text());
												if(oCurObj.data.bDisplayAttempts)
												{
													var iAttemptsLeft = 0;
													for(var i=0; i<CLZ.oStore[sId].A.length; i++)
													{
														if(CLZ.oStore[sId].A[i].u=="c") continue;
														iAttemptsLeft = CLZ.oStore[sId].A.length - i;
														break;
													}
													jQBlock.find(".cl-stats-q-attempts").html(oCurObj.jxParams.children("text_constant_attemptsmax").text() + " " + CLZ.oStore[sId].A.length + "<br/>" + oCurObj.jxParams.children("text_constant_attemptsleft").text() + " " + iAttemptsLeft);
												}
												if(oCurObj.data.bDisplayTimers && CLZ.oStore[sId].t!=null)
												{
													jQBlock.find(".cl-stats-q-timing").html(oCurObj.jxParams.children("text_constant_duration").text() + " " + CLZ.oStore[sId].t + "<br/>" + oCurObj.jxParams.children("text_constant_latency").text() + " " + CLZ.oStore[sId].e);
												}
												else
												{
													jQBlock.find(".cl-stats-q-timing").hide();
												}
												if(oCurObj.data.bDisplayScores)
												{
													jxObjectives = jxParams.find("q_scoreboards > item");
													var sObjId;
													var jRow;
													var jCell;
													var jScoreTable = jQBlock.find(".cl-stats-obj-table");
													if(jScoreTable.children("tbody").length!=0)
													{
														jScoreTable = jScoreTable.children("tbody:first");
													}
													for(var i=0; i<jxObjectives.length; i++)
													{
														sObjId = $(jxObjectives[i]).children("q_board").text();
														jRow = jScoreTable.children("tr[data-obj-id='" + sObjId + "']");
														if(jRow.length==0)
														{
															jRow = $(document.createElement("tr")).attr({ "data-obj-id": sObjId }).appendTo(jScoreTable);
															$(document.createElement("td")).addClass("cl-stats-obj-obj").attr({ "data-obj-id": sObjId }).html(CLJ[sObjId].sName).appendTo(jRow);
															$(document.createElement("td")).addClass("cl-stats-obj-score").attr({ "data-obj-id": sObjId }).appendTo(jRow);
														}
														if(CLJ[sObjId].oSrc[sId]!=null)
														{
															jRow.children(".cl-stats-obj-score").html(CLJ[sObjId].oSrc[sId].nRawScore);
														}
														else
														{
															jRow.children(".cl-stats-obj-score").html("0");
														}
													}
												}
												else
												{
													jQBlock.find(".cl-stats-right").hide();
												}
											}
											if(oCurObj.data.bDisplayVariants)
											{
												var jVarTable = jQBlock.find(".cl-results-q-variants-table");
												if(jVarTable.length!=0)
												{
													jVarTable.remove();
												}
												jVarTable = $(document.createElement("table")).addClass("cl-results-q-variants-table").appendTo(jQBlock.find(".cl-results-variants .cl-results-contents"));
												jVarTable = $(document.createElement("tbody")).appendTo(jVarTable);
												fnFillVars({ sId: sId, sType: sType, jVarTable: jVarTable, jxParams: jxParams, oStore: CLZ.oStore[sId] });
											}
											break;
										}
										case "i":
										{
											jQBlock.find(".cl-results-na").html("Не закончен");
											break;
										}
									}
								}
							}
						}
					}
				});
			}
			break;
		}
	}
	return true;
}
